mapper.select:
select t.id task_id, t.job_id, a.process_id, t.node_id, t.child_node_id,
        a.`group` node_group, a.name node_name, a.type node_type
from wf_act_task t
         left join wf_def_node a on t.node_id = a.id


        and t.id = #{taskId}


        and t.id in
        #{item}


        and t.job_id = #{jobId}


        and t.state = #{state}


where t.id = ''

mapper.update:
update wf_act_task
    set state = #{state}, update_time = now()
where job_id = #{jobId} and node_id = #{nodeId}

update wf_act_task
    set state = 1, update_time = now()
where job_id = #{jobId} and node_id = #{nodeId}

mapper.select:
select count(1)
    from wf_act_task

    and state = 1
    and job_id = #{jobId}
    and node_id in
    #{item}
