mapper.update:
update wf_act_task
    set state = #{state}, update_time = now()
where job_id = #{jobId} and node_id = #{nodeId}

update wf_act_task
    set state = 1, update_time = now()
where job_id = #{jobId} and node_id = #{nodeId}

mapper.select:
select count(1)
    from wf_act_task

    and state = 1
    and job_id = #{jobId}
    and node_id in
    #{item}
